sqlMap.select:
Select DISTINCT B.ISSUYM AS ISSUYM, A.APNO AS APNO, A.EVTNAME AS EVTNAME, A.EVTIDNNO AS EVTIDNNO, A.EVTBRDATE AS EVTBRDATE, A.CHKMAN AS CHKMAN, A.PAYYMS AS PAYYMS, A.PROCSTAT AS PROCSTAT, A.PROCSTATMK From ( Select DISTINCT APNO, EVTNAME, EVTIDNNO, EVTBRDATE, CHKMAN, ISSUYM, PAYYMS, PROCSTAT, (case when PROCSTAT < '20' then 'N' when PROCSTAT >= '20' then 'Y' end) AS PROCSTATMK From BAAPPBASE Where APNO = #apNo:VARCHAR# And SEQNO = '0000' ) A, (Select APNO, ISSUYM From BADAPR Where APNO = #apNo:VARCHAR# And SEQNO = '0000'
ISSUYM = #issuYm:VARCHAR#
) B Where A.APNO = B.APNO

SELECT A.BAAPPBASEID, A.APNO, A.ISSUYM, A.EVTNAME, A.EVTIDNNO, A.CASETYP, A.PROCSTAT, A.CHKMAN, A.CHKDATE, A.MANCHKMK, NVL(A.MEXCLVL, '0') MEXCLVL, A.MEXCLVL, SUBSTR(A.APNO,1,1) PAGEPAYKIND, B.BALP0D020ID, B.VERISEQ, B.TISSUEAMT, B.TAPLPAYAMT, B.PAYSYM, B.PAYEYM, B.RPTDATE, B.PAGENO, A.RECHK, NVL(A.EXEMK, '0') EXEMK, DECODE(B.EXEMK,'1','Y','N') REBOX, DECODE(B.EXEMK,'2','Y','N') EXEBOX, A.PAYKIND FROM (SELECT A1.BAAPPBASEID, A1.APNO, A1.ISSUYM, A1.EVTNAME, A1.EVTIDNNO, A1.MANCHKMK, A1.CASETYP, A1.PROCSTAT, A1.CHKMAN, A1.CHKDATE, A1.MEXCLVL, A1.PAYKIND, (SELECT DECODE(SIGNL2, NULL, '', 'Y') As "SIGNL1" FROM BCCMF01 WHERE PRPNO = #empId:VARCHAR# AND INSKD = '1' AND (PAYTYP = (Select min(DECODE(PAYTYP, '00', '99', PAYTYP)) As "PAYTYP" From BCCMF01 where PRPNO = #empId:VARCHAR# AND INSKD = '1' And (PAYTYP = SUBSTR(A1.APNO, 1, 1) OR (PAYTYP = '99'))))) AS RECHK, (SELECT (CASE WHEN SIGNL2>SIGNL3 THEN SIGNL2 ELSE SIGNL3 END) FROM BCCMF01 WHERE PRPNO = #empId:VARCHAR# AND INSKD = '1' AND (PAYTYP = (Select min(DECODE(PAYTYP, '00', '99', PAYTYP)) As "PAYTYP" From BCCMF01 where PRPNO = #empId:VARCHAR# AND INSKD = '1' And (PAYTYP = SUBSTR(A1.APNO, 1, 1) OR (PAYTYP = '99'))))) AS EXEMK FROM BAAPPBASE A1 WHERE A1.APNO IN (SELECT APNO FROM BALP0D020 WHERE APNO = #apNo:VARCHAR# AND PAYCODE = #payCode:VARCHAR#
AND ((CASETYP <> '3' AND CASETYP <> '6' AND PROCMK <> '2') OR CASETYP = '3' OR CASETYP = '6' OR MANCHKMK = 'N')
) AND A1.SEQNO = '0000' AND A1.PROCSTAT IN ('20','30','40')
AND NVL(TRIM(A1.MANCHKMK),' ')<>' ' AND A1.MANCHKMK IS NOT NULL AND ((A1.MEXCLVL<=(SELECT (CASE WHEN SIGNL2>SIGNL3 THEN SIGNL2 ELSE SIGNL3 END) FROM BCCMF01 WHERE PRPNO = #empId:VARCHAR# AND INSKD = '1' AND (PAYTYP = (Select min(DECODE(PAYTYP, '00', '99', PAYTYP)) As "PAYTYP" From BCCMF01 where PRPNO = #empId:VARCHAR# AND INSKD = '1' And (PAYTYP = SUBSTR(A1.APNO, 1, 1) OR (PAYTYP = '99')))))) OR(MEXCLVL>(SELECT (CASE WHEN SIGNL2>SIGNL3 THEN SIGNL2 ELSE SIGNL3 END) FROM BCCMF01 WHERE PRPNO = #empId:VARCHAR# AND INSKD = '1' AND (PAYTYP = (Select min(DECODE(PAYTYP, '00', '99', PAYTYP)) As "PAYTYP" From BCCMF01 where PRPNO = #empId:VARCHAR# AND INSKD = '1' And (PAYTYP = SUBSTR(A1.APNO, 1, 1) OR (PAYTYP = '99'))))) AND(SELECT DECODE(SIGNL2, NULL, '', 'Y') As "SIGNL1" FROM BCCMF01 WHERE PRPNO = #empId:VARCHAR# AND INSKD = '1' AND (PAYTYP = (Select min(DECODE(PAYTYP, '00', '99', PAYTYP)) As "PAYTYP" From BCCMF01 where PRPNO = #empId:VARCHAR# AND INSKD = '1' And (PAYTYP = SUBSTR(A1.APNO, 1, 1) OR (PAYTYP = '99')))))='Y'))
) A, (SELECT B1.BALP0D020ID, B1.APNO, B1.ISSUYM, B1.VERISEQ, B1.RPTDATE, B1.PAGENO, B1.PAYSYM, B1.PAYEYM, B1.TISSUEAMT, B1.TAPLPAYAMT, B1.CHKDATE, B1.CHKMAN, B1.EXEMK FROM BALP0D020 B1 WHERE B1.APNO = #apNo:VARCHAR# AND B1.PAYCODE = #payCode:VARCHAR#
AND (((B1.CASETYP <> '3' AND B1.CASETYP <> '6' AND B1.PROCMK <> '2') OR (B1.CASETYP = '3' OR B1.CASETYP = '6' OR B1.MANCHKMK = 'N')) AND B1.BALP0D020ID = (SELECT MAX(B11.BALP0D020ID) FROM BALP0D020 B11 WHERE B11.APNO = B1.APNO AND B11.PAYCODE = #payCode:VARCHAR#)) ) B WHERE A.APNO = B.APNO AND A.CHKDATE = B.CHKDATE AND A.CHKMAN = B.CHKMAN ORDER BY B.BALP0D020ID

SELECT * FROM( SELECT APNO, SEQNO, APPDATE, EVTNAME, EVTIDNNO, EVTBRDATE, EVTJOBDATE, EVTDATE, EVTELIGIBLEDATE, EVTDIEDATE, ASSIGNNAME, ASSIGNIDNNO, ASSIGNBRDATE, PAYTYP, BENNAME, BENIDNNO, BENBRDATE, BENNATIONTYP, COMMZIP, COMMADDR, CASETYP, LSUBNO, COMBAPMARK, APUBNO, IDNCHKYM, NOTIFYFORM, ISSUYM, BENEVTREL, APITEM, GRDNAME, PAYYMS, UNQUALIFIEDCAUSE, ISSUNOTIFYINGMK, BAAPPBASEID, BENNATIONCODE, BENDIEDATE, DIGAMYDATE, CLOSECAUSE, CLOSEDATE, ACCREL, EVTYP, DATACOUNT, BENSEX, ACCEPTMK, EVTIDS, rank() over(PARTITION BY APNO,BENNAME ORDER BY SEQNO) top FROM (Select A.APNO, A.SEQNO, A.APPDATE, A.EVTNAME, A.EVTIDNNO, A.EVTBRDATE, A.EVTJOBDATE, A.EVTDATE, A.EVTELIGIBLEDATE, A.EVTDIEDATE, A.ASSIGNNAME, A.ASSIGNIDNNO, A.ASSIGNBRDATE, A.PAYTYP, NVL(A.GRDNAME,A.BENNAME) AS BENNAME, A.BENIDNNO, A.BENBRDATE, A.BENNATIONTYP, NVL((SELECT A1.COMMZIP FROM BAAPPBASE A1 WHERE A1.APNO = A.APNO AND A1.BENNAME = NVL(A.GRDNAME, A.BENNAME) AND ROWNUM = 1),A.COMMZIP) COMMZIP, NVL((SELECT A1.COMMADDR FROM BAAPPBASE A1 WHERE A1.APNO = A.APNO AND A1.BENNAME = NVL(A.GRDNAME, A.BENNAME) AND ROWNUM = 1),A.COMMADDR) COMMADDR, A.CASETYP, A.LSUBNO, A.COMBAPMARK, A.APUBNO, A.IDNCHKYM, A.NOTIFYFORM, A.ISSUYM, A.BENEVTREL, A.APITEM, A.GRDNAME, A.PAYYMS, A.UNQUALIFIEDCAUSE, A.ISSUNOTIFYINGMK, A.BAAPPBASEID, A.BENNATIONCODE, A.BENDIEDATE, C.DIGAMYDATE, A.CLOSECAUSE, A.CLOSEDATE, A.ACCREL, C.EVTYP, A.EVTIDS, (SELECT COUNT(*) FROM BACHKFILE D WHERE D.APNO = A.APNO AND D.SEQNO = A.SEQNO AND D.ISSUYM = A.ISSUYM AND D.PAYYM = (SELECT MAX(B.PAYYM) FROM BACHKFILE B WHERE B.SEQNO = '0000' AND B.APNO = A.APNO AND B.ISSUYM = A.ISSUYM) AND D.CHKCODE = '25') DATACOUNT, A.BENSEX, (SELECT ACCEPTMK FROM BADAPR WHERE APNO = A.APNO AND SEQNO = A.SEQNO AND ISSUYM = A.ISSUYM AND PAYYM = A.PAYYM AND VERISEQ = (SELECT MAX(TO_NUMBER(VERISEQ)) FROM BADAPR WHERE APNO = A.APNO AND SEQNO = A.SEQNO AND ISSUYM = A.ISSUYM AND PAYYM = A.PAYYM) AND PAYKIND = A.PAYKIND) ACCEPTMK From BAAPPBASE A, BAAPPEXPAND C Where A.APNO = #apNo:VARCHAR# AND A.APNO = C.APNO(+) AND A.BAAPPBASEID = C.BAAPPBASEID(+) AND A.SEQNO = C.SEQNO(+) AND A.BENEVTREL BETWEEN '2' AND '7'
AND A.SEQNO <> '0000'
)) WHERE top = 1 Order By SEQNO
