sqlMap.select:
SELECT DISTINCT T2.NITRMY, T2.NITRMM, T2.ITRMY, T2.ITRMD, T2.APLPAYSENIY, T2.APLPAYSENIM, T2.NOLDTY, T2.NOLDTM, T2.VALSENIY, T2.VALSENIM, T2.INSAVGAMT, T4.EVTYP, T4.PRTYPE, T1.EVTIDNNO FROM (SELECT APNO, SEQNO, ISSUYM, EVTIDNNO FROM BAAPPBASE WHERE APNO = #apNo:VARCHAR# AND SEQNO = '0000'
PAYKIND IN ('35','36','38')
) T1, (SELECT DISTINCT APNO, VERISEQ, ISSUYM, NITRMY, NITRMM, ITRMY, ITRMD, APLPAYSENIY, APLPAYSENIM, NOLDTY, NOLDTM, VALSENIY, VALSENIM, INSAVGAMT FROM BADAPR WHERE APNO = #apNo:VARCHAR# AND SEQNO = '0000' AND (SUPRECMK is null or trim(SUPRECMK) = '') AND PAYYM = (select MIN(PAYYM) from BADAPR where APNO = #apNo:VARCHAR# AND SEQNO = '0000' and ISSUYM = (SELECT ISSUYM FROM BAAPPBASE WHERE APNO = #apNo:VARCHAR# AND SEQNO = '0000'
PAYKIND IN ('35','36','38')
))
PAYKIND IN ('35','36','38')
) T2, (SELECT MAX(TO_NUMBER(VERISEQ)) AS VERISEQ, ISSUYM FROM BADAPR WHERE APNO = #apNo:VARCHAR# AND SEQNO = '0000'
PAYKIND IN ('35','36','38')
GROUP BY ISSUYM ) T3, (SELECT APNO, SEQNO, EVTYP, PRTYPE FROM BAAPPEXPAND WHERE APNO = #apNo:VARCHAR# AND SEQNO = '0000' ) T4 WHERE T1.APNO = T2.APNO AND T1.ISSUYM = T2.ISSUYM AND T1.ISSUYM = T3.ISSUYM AND T2.VERISEQ = T3.VERISEQ AND T2.ISSUYM = T3.ISSUYM AND T1.APNO = T4.APNO AND T1.SEQNO = T4.SEQNO
